        $('form.checkout').on('checkout_place_order', function(e) {
            // Remove previous errors
            $('.custom-validation-error').remove();

            // Get the phone and postcode fields
            var phoneField = $('#billing_phone');
            var postcodeField = $('#billing_postcode');

            // Validate phone number
            var phone = phoneField.val().replace(/\D/g, '');
            if (!/^\d{10}$/.test(phone) || phone.length !== 10) {
                phoneField.after('<div class="custom-validation-error" style="color: red;">Please enter a valid 10-digit phone number without the country code.</div>');
                e.preventDefault();
                return false;
            }

            // Validate PIN code
            var postcode = postcodeField.val().replace(/\D/g, '');
            if (!/^\d{6}$/.test(postcode) || postcode.length !== 6) {
                postcodeField.after('<div class="custom-validation-error" style="color: red;">Please enter a valid 6-digit PIN code.</div>');
                e.preventDefault();
                return false;
            }

            return true;
        });
    });
